import React from "react";
import MaterialTable from "material-table";
import { RootRef } from "@material-ui/core";
// import { number } from "prop-types";

export default function MaterialTableDemo() {
  const [state, setState] = React.useState({
    columns: [
      // { title: "Serial No", field: "serial", type: "numeric" },
      { title: "Serial No", field: "serial" },
      { title: "Name", field: "name", type: "string" },
      { title: "Phone", field: "phone", type: "undefined" },
      { title: "Email", field: "email", type: "any" }
    ],
    data: [
      {
        // serial: 1,
        name: "Mehmet",
        phone: 42343234,
        email: "Baran@gmail.com"
      },
      {
        // serial: 3,
        name: "Zerya Betül",
        phone: 42343234,
        email: "Baran@gmail.com"
      }
    ]
  });

  return (
    <MaterialTable
      title="Mall programming test"
      columns={state.columns}
      data={state.data}
      serialNumber={rowIndex}
      editable={{
        onRowAdd: newData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...state.data];
              data.push(newData);
              setState({ ...state, serialNumber, data });
            }, 600);
          }),

        onRowUpdate: (newData, oldData) =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...state.data];
              data[data.indexOf(oldData)] = newData;
              setState({ ...state, data });
            }, 600);
          }),

        onRowDelete: oldData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...state.data];
              data.splice(data.indexOf(oldData), 1);
              setState({ ...state, data });
            }, 600);
          })
      }}
    />
  );
}
